import React, {useState} from 'react';

interface IProduct {
    sno : string;
    name : string;
    image : string;
    price : number;
    qty : number;
}
interface IProps {}
interface IState {
    products : IProduct[]
}

let ShippingCart:React.FC<IProps> = () => {
    let [productState , setProductState] = useState<IState>({
        products : [
            {
                sno : 'AA00AB12',
                name : 'Mi Watch',
                image : 'data:image/jpeg;base64,',
                price : 2500,
                qty : 2
            },
            {
                sno : 'AA00AB13',
                name : 'Apple Watch',
                image : 'data:image/jpeg;base64,',
                price : 8500,
                qty : 2
            },
            {
                sno : 'AA00AB14',
                name : 'Oppo Watch',
                image : 'data:image/jpeg;base64,',
                price : 3500,
                qty : 2
            }
        ]
    });

    let incrQty = (sno:string) => {
        let incrItems:IProduct[] = productState.products.map(product => {
            if(product.sno === sno){
                return {
                    ...product,
                    qty : product.qty + 1
                }
            }
            else{
                return product;
            }
        });
        setProductState({
            products : incrItems
        });
    };

    let decrQty = (sno:string) => {
        let decrItems:IProduct[] = productState.products.map(product => {
            if(product.sno === sno){
                return {
                    ...product,
                    qty : (product.qty - 1 > 0) ? product.qty - 1 : 1
                }
            }
            else{
                return product;
            }
        });
        setProductState({
            products : decrItems
        });
    };

    let {products} = productState;
    return (
        <React.Fragment>
            <section>
                <div className="container mt-3">
                    <div className="row">
                        <div className="col">
                            <p className="h3">Shopping Cart</p>
                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Adipisci dolorem dolorum eum iusto non omnis porro? Atque aut consequuntur earum harum modi nisi optio qui rem rerum soluta! Dicta, magni!</p>
                        </div>
                    </div>
                </div>
            </section>
            <section>
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <table className="table table-hover text-center table-striped">
                                <thead className="bg-dark text-white">
                                <tr>
                                    <th>SNO</th>
                                    <th>Image</th>
                                    <th>Name</th>
                                    <th>Price</th>
                                    <th>Qty</th>
                                    <th>Total</th>
                                </tr>
                                </thead>
                                <tbody>
                                {
                                    products.map((product) => {
                                        return (
                                            <tr key={product.sno}>
                                                <td>{product.sno}</td>
                                                <td>
                                                    <img src={product.image} alt="" width="50" height="50"/>
                                                </td>
                                                <td>{product.name}</td>
                                                <td>{product.price}</td>
                                                <td>
                                                    <i className="fa fa-minus-circle mx-1" onClick={decrQty.bind(this,product.sno)}/>
                                                    {product.qty}
                                                    <i className="fa fa-plus-circle mx-1" onClick={incrQty.bind(this,product.sno)}/>
                                                </td>
                                                <td>{product.price * product.qty}</td>
                                            </tr>
                                        )
                                    })
                                }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    );
};
export default ShippingCart;